    @RequiresApi(api = Build.VERSION_CODES.O)
    public Personal(int day, int month, String name) {
        //this order must be followed
        listing = createListing(day, month);
        period = determinePeriod(day, month);
        headline = createHeadline(period, name);
        headlineInYoruba = createHeadlineInYoruba(period, name);
        headlineInBulgarian = createHeadlineInBulgarian(period, name);
        details = constructDetails(period);
        detailsInYoruba = constructDetailsInYoruba(period);
        detailsInBulgarian = constructDetailsInBulgarian(period);
    }

    @RequiresApi(api = Build.VERSION_CODES.O)
    private List<String> createListing(int day, int month) {
        dates = new ArrayList<>();
        List<String> intervals = new ArrayList<>();
        int counter = 0;
        for (int i = 0; i < 364; i += 52) {
            counter++;
            LocalDate startDate = LocalDate.of(LocalDate.now().getYear(), month, day).plusDays(i);
            //LocalDate endDate = startDate.plusDays(51);
            LocalDate endDate = startDate.plusDays(counter >= 7 ? 52 : 51);

            DateTimeFormatter formatter = DateTimeFormatter.ofPattern("MMMM d");
            intervals.add(startDate.format(formatter) + "  to  " + endDate.format(formatter));

            dates.add(startDate);
        }

        return intervals;
    }

